class Solution:    def longestCommonPrefix(self, strs):        ans = ""        sorted_arr = sorted(strs)        first = sorted_arr[0]        last = sorted_arr[-1]        for i in range(min(len(first), len(last))):            if first[i] == last[i]:                ans += first[i]            else:                return anssol = Solution()print(sol.longestCommonPrefix(["flower","flow","flight"]))